mapper.select:

select title, author, content, attach_file, w_date
from post
where no = #{no }


select no, title, author, content, w_date
from post
where blog_id = #{blog_id }


select count(no)
from post
where blog_id = #{blog_id }

choose.when:

	update post
 	set title = #{title},
 	attach_file = #{attach_file},
 	content = #{content}
 	where no = #{no}

choose.otherwise:

	update post
 	set title = #{title},
	 	content = #{content}
 	where no = #{no}
