mapper.select:

select id, name
from user
where id = #{id }
and password = #{password }


select id, name, password
from user
where id = #{id }


select id, name, password
from user
where id = #{id }

choose.when:

update user
set name = #{name }, 
    password = #{password }
where id = #{id }

choose.otherwise:

update user
set name = #{name }
where id = #{id }
